package StringFunctionsAll;

public class StringFunctionTrim {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 String name=" Snehika Paspunuri ";
 System.out.println(name.length());
 System.out.println(name.contains("Paspunuri"));
 System.out.println(name.trim());//removes space at from and end of word not between.
 
 String title_of_page= " Hello world it's me ";
 System.out.println(title_of_page.trim());
	}

}
