package basicknowledge;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double a=  90;
System.out.println(a);
int age=80;
double ageofperson=(double)  age;
System.out.println(ageofperson);

byte a1=22;
System.out.println(a1);
long ageofgranny=(byte)  a1;
System.out.println(ageofgranny);
ageofgranny=1000;
System.out.println(ageofgranny);
System.out.println("========");
long a4=584793;
System.out.println(a4);
byte newbyte=(byte) a4;
System.out.println(newbyte);
System.out.println("==========");
int a2=9897;
System.out.println(a2);
float myage=(float) a2;
System.out.println(myage);

double pi=3.14;
System.out.println(pi);
int pinewvalue= (int) pi;
System.out.println(pinewvalue);

	}

}
